mapper.select:
SELECT count(distinct c.cafeId)
FROM cafe c
LEFT JOIN (
SELECT cafeId, featureId
from cafeFeature
group by cafeId, featureId
) cf ON c.cafeId = cf.cafeId
LEFT JOIN (
SELECT featureId, featureName
from feature
group by featureName, featureId
) f ON cf.featureId = f.featureId
WHERE c.study = #{studyEnable}


        AND c.cafeType = #{cafeType}


        AND c.cafeType IN ('G','P')



    AND EXISTS (
    SELECT 1
    FROM cafeTable ct
    WHERE ct.cafeId = c.cafeId AND ct.tableType = #{preferSeat}
    )


    AND c.startTime <= #{startTime} AND c.endTime >= #{endTime}


    AND NOW() >= c.startTime AND NOW() <= c.endTime


    AND f.featureName IN

        #{feature}



    AND c.cafeName LIKE CONCAT('%', #{word}, '%')

SELECT c.cafeId, c.cafeName, c.cafeType, c.startTime, c.endTime, c.address, c.longitude, c.latitude, c.cafeTel, c.study FROM cafe c LEFT JOIN ( SELECT cafeId, featureId from cafeFeature group by cafeId, featureId ) cf ON c.cafeId = cf.cafeId LEFT JOIN ( SELECT featureId, featureName from feature group by featureName, featureId ) f ON cf.featureId = f.featureId WHERE c.study = #{searchRequestDto.studyEnable}
AND c.cafeType = #{searchRequestDto.cafeType}
AND c.cafeType IN ('G','P')
AND EXISTS ( SELECT 1 FROM cafeTable ct WHERE ct.cafeId = c.cafeId AND ct.tableType = #{searchRequestDto.preferSeat} )
AND c.startTime <= #{searchRequestDto.startTime} AND c.endTime >= #{searchRequestDto.endTime}
AND NOW() >= c.startTime AND NOW() <= c.endTime
AND f.featureName IN
#{feature}
AND c.cafeName LIKE CONCAT('%', #{searchRequestDto.word}, '%')
LIMIT #{pager.startRowNo}, #{pager.rowsPerPage}
